    @Test
    public void isBlock() {
        String html = "<div><p><span>Hello</span>";
        Document doc = Jsoup.parse(html);
        Assert.assertTrue(doc.selectFirst("div").isBlock());
        Assert.assertTrue(doc.selectFirst("p").isBlock());
